_TerroirAvatar.vertexProgram:
varying vec2 Texcoord;

    uniform int Time;
    uniform float fps;

    void main( void )
    {
        float time = (float(Time)/1000.0)*fps;
        Texcoord.x = gl_MultiTexCoord0.x ;
        Texcoord.y = gl_MultiTexCoord0.y ;

        gl_Position = ftransform();	
        gl_ClipVertex = gl_ModelViewMatrix*gl_Vertex;
    }

_TerroirAvatar.fragmentProgram:
varying vec2 Texcoord;

    uniform sampler2D waterMap;
    uniform sampler2D starryMap;
    uniform sampler2D forestMap;

    uniform int Time;

    uniform float SplotchCellSize;
    uniform int SplotchWaveNr;

    uniform vec3 areaActive;

    void main( void )
    {
        vec4 color = vec4(0.0, 0.0, 0.0, 1.0); // default: black

        if (Texcoord.y < 0.5) {
            if (areaActive.x != 0.0)
                color = texture2D(starryMap, Texcoord);
        } else if (Texcoord.y < 0.535) {
            if (areaActive.y != 0.0)
                color = texture2D(waterMap, Texcoord);
        } else {
            if (areaActive.z != 0.0)
                color = texture2D(forestMap, Texcoord);
         } 

        gl_FragColor = color;

    }
